mapper.update:
update  qpPosition
set qpLatitude=#{qpLatitude},
	qpLongitude=#{qpLongitude},
	bCode=#{bCode},
	hCode=#{hCode}
where qpId=#{qpId}

update  qpPosition
set qpStatus = 1
where qpId=#{qpId}

update  qpPosition
set qpStatus = 0
where qpId=#{qpId}

update  qpPosition
set qpStatus = #{qpStatus}
where qpId=#{qpId}
